html:
{% set var_disc = namespace(value=0) %}
<style>
	.print-format table, .print-format tr, 
	.print-format td, .print-format div, .print-format p {
		font-family: Tahoma, sans-serif;
		line-height: 150%;
		vertical-align: middle;
	}
	@media screen {
		.print-format {
			width: 4in;
			padding: 0.25in;
			min-height: 8in;
		}
	}
</style>

{% if letter_head %}
    {{ letter_head }}
{% endif %}

<p class="text-center" style="margin-bottom: 1rem">
	<b>{{ doc.company }}<br>
	{{ _("Pre Cuenta") }}<br></b>
</p>
<p>
	<b>{{ _("Orden No") }}:</b> {{ doc.name }}<br>
	<b>{{ _("Cliente") }}:</b> {{ doc.customer_name }}<br>
	<b>{{ _("Mesa") }}:</b> {{ doc.table_description }}
</p>
<table class="table table-condensed cart no-border">
	<thead>
		<tr>
		    <th width="3%" class="text-right"><b>{{ _("[Qty]") }}</b></th>
			<th width="47%"><b>{{ _("Item") }}</b></th>
			<th width="25%" class="text-right"><b>{{ _("PU") }}</b></th>
			<th width="25%" class="text-right"><b>{{ _("Amount") }}</b></th>
		</tr>
	</thead>
	<tbody>
		{%- for item in doc.entry_items -%}
		<tr>
		    <td class="text-center">[{{ item.qty }}]</td>
			<td>
				{%- if item.item_name != item.item_code -%}
					{{ item.item_name }}
				{%- endif -%}
				{%- if item.discount_percentage > 0 -%}
					({{ item.discount_percentage }}%)
				{%- endif -%}
				{%- if item.serial_no -%}
					<br><b>{{ _("SR.No") }}:</b><br>
					{{ item.serial_no | replace("\n", ", ") }}
				{%- endif -%}
			</td>
			<td class="text-right">{{ item.get_formatted("price_list_rate") }}</td>
			<td class="text-right">{{ item.get_formatted("amount") }}</td>
		</tr>
		{%- endfor -%}
	</tbody>
</table>
<table class="table table-condensed no-border">
	<tbody>
		<tr>
			<td class="text-right" style="width: 75%">
				{{ _("Tax") }}
			</td>
			<td class="text-right">
				{{ doc.get_formatted("tax") }}
			</td>
		</tr>
		{%- if doc.discount > 0 -%}
		<tr>
			<td class="text-right" style="width: 75%">
				<b>{{ _("Grand Total") }}</b>
			</td>
			<td class="text-right">
				{{ doc.get_formatted("amount") }}<br>
			</td>
		</tr>
		<tr>
			<td class="text-right" style="width: 75%">
				{{ _("Discount") }}<br>
				<b>{{ _("Total - Descuento") }}</b>
			</td>
			<td class="text-right">
				{{ doc.get_formatted("discount") }}<br>
				{% set var_disc.value = doc.amount - doc.discount %}
				<b>{{ "S/. %.2f"|format(var_disc.value|float) }}</b>
			</td>
		</tr>
		{%- endif -%}
		{%- if doc.discount_global_percent > 0  -%}
		<tr>
			<td class="text-right" style="width: 75%">
				<b>{{ _("Grand Total") }}</b>
			</td>
			<td class="text-right">
				{{ doc.get_formatted("amount") }}<br>
			</td>
		</tr>
		<tr>
			<td class="text-right" style="width: 75%">
				{{ _("Discount") }}<br>
				<b>{{ _("Total - Descuento") }}</b>
			</td>
			<td class="text-right">
				{{ doc.get_formatted("discount_global_percent") }}<br>
				{% set var_disc.value = doc.amount * (1-(doc.discount/100)) %}
				<b>{{ "S/. %.2f"|format(var_disc.value|float) }}</b>
			</td>
		</tr>
		{%- endif -%}
	</tbody>
</table>
<p class="text-center">{{ _("ESTE DOCUMENTO NO ES COMPROBANTE DE PAGO") }}</p><br>
<p>{{ _("RUC:") }}</p><br>
<p>{{ _("RAZON SOCIAL:") }}</p><br>
<p class="text-center">{{ _("GRACIAS POR SU PREFERENCIA, VUELVA PRONTO!") }}</p>
